import logo from './logo.svg';
import './App.css';

import Article from './components/ArticleComponent'
import Header from './components/HeaderComponent'
import Footer from './components/FooterComponent'
import Themes from './components/ThemesComponent'

let status = [false, false, false, false, false, false, false, false, false];


function App(){


    function returnDataToApp(evt){
        return (
            <div>
                <Header />
                <Article currentStatus={evt} />
                <Themes />
                <Footer />
            </div>
        )
    }

    if(status == [false, false, false, false, false, false, false, false, false])
        return(
            <div>
                <Header />
                <Article currentStatus={status}/>
                <Themes callBack={returnDataToApp}/>
                <Footer/>
            </div>
        )
    else
        return(
            <div>
                <Header />
                <Article currentStatus={status}/>
                <Themes callBack={returnDataToApp}/>
                <Footer/>
            </div>
        );

}


export default App;
