import React from 'react';
import ReactDOM from 'react-dom';

import logo from './logo.svg';
import './App.css';

import Article from './components/ArticleComponent'
import Header from './components/HeaderComponent'
import Footer from './components/FooterComponent'
import Themes from './components/ThemesComponent'

let status = [false,false,false,false,false,false,false,false,false];

class App extends React.Component{

    constructor() {
        super();
        this.state = {st: status};
    }

    returnDataToApp(evt){
        this.setState();
        console.log(this.state.st);
        return evt;
    }

    render(){

        return(
            <div>
                <Header />
                <Article curr={this.state}/>
                <Themes callBack={returnDataToApp}/>
                <Footer/>
            </div>
        )

    }

}


export default App;
